mapper.select:
SELECT * FROM note



        ORDER BY created_at


                ${params.order}


                ASC




LIMIT #{limit} OFFSET ${offset}

select.if:
SELECT question_id
FROM note
WHERE author_id = #{authorId}
AND question_id IN

    #{questionId}

mapper.select:
SELECT *
FROM note
WHERE question_id = #{questionId}
  AND author_id = #{authorId}

mapper.update:
UPDATE note
SET content = #{content}
WHERE note_id = #{noteId}

UPDATE note
SET like_count = like_count + 1
WHERE note_id = #{noteId}

UPDATE note
SET like_count = like_count - 1
WHERE note_id = #{noteId}

UPDATE note
SET collect_count = collect_count + 1
WHERE note_id = #{noteId}

UPDATE note
SET collect_count = collect_count - 1
WHERE note_id = #{noteId}
